#include <iostream>
#include <vector>
#include <string>
#include <string.h>
#include <algorithm>
using namespace std;
const int H = 205;
const int W = 205;
int maze[H][W],h,w,colorCount;
bool colorVis[H],vis[H][W];
const int dir[4][2] = { {0,1},{1,0},{-1,0},{0,-1} };
const char hieroglyphMap[] = {'W','A','K','J','S','D'};

bool judge(const int x, const int y){
    return (x>-1&&x<h&&y>-1&&y<w&&!vis[x][y]);
}

void dfs(int x,int y,int color){
    maze[x][y] = color;
    for(int i = 0; i < 4; i++){
        int xx = x+dir[i][0];
        int yy = y+dir[i][1];
        if(judge(xx,yy) && !maze[xx][yy]){
            vis[xx][yy] = true;
            dfs(xx,yy,color);
        }
    }
}

void countDiffentColor(int x,int y){
    for(int i = 0; i < 4; i++){
        int xx = x + dir[i][0];
        int yy = y + dir[i][1];
        if(judge(xx, yy)){
            if(maze[xx][yy] != 1){
                if(!colorVis[maze[xx][yy]]) colorCount++;
                colorVis[maze[xx][yy]] = true;
            }
            else{
                vis[xx][yy] = true;
                countDiffentColor(xx, yy);
            }
        }
    }
}



int main(int argc, const char * argv[]) {
    int caseNumber = 1;
    while(~scanf("%d%d",&h,&w)){
        if(0 == h && 0 == w) break;
        memset(maze, 0, sizeof(int) * H * W);
        char strs[51];
        for(int i = 0; i < h; i++){
            scanf("%s",strs);
            for(int j = 0; j < w; j++){
                int tmp = 0;
                if(strs[j]>='0' && strs[j]<='9') tmp = strs[j] - '0';
                else tmp = 10+strs[j]-'a';

                for(int k = 0; k < 4 ; k++){
                    maze[i+1][4*j+k+1] = ((tmp>>(3-k))&1);
                }
            }
        }

        h+=2, w*=4,w+=2;
        int color = 2;
        memset(vis, false, sizeof(bool) * H * W);
        for(int i = 0; i < h; i++){
            for(int j = 0; j < w; j++){
                if(!maze[i][j]){
                    dfs(i,j,color);
                    color++;
                }
            }
        }
        vector<char> ans;
        memset(vis, false, sizeof(bool) * H * W);
        for(int i = 0; i < h; i++){
            for(int j = 0; j < w; j++){
                if(maze[i][j] == 1 && !vis[i][j]){
                    colorCount = 0;
                    memset(colorVis, false, sizeof(bool) * H);
                    countDiffentColor(i,j);
                    ans.push_back(hieroglyphMap[colorCount - 1]);
                }
            }
        }
        sort(ans.begin(), ans.end());
        string str(ans.begin(),ans.end());
        cout<<"Case "<<caseNumber++<<": "<<str<<endl;
    }
    return 0;
}

