//
//  main.cpp
//  hdu 4527
//
//  Created by miaoyou.gmy on 15/8/30.
//  Copyright (c) 2015 miaoyou.gmy. All rights reserved.
//

#include <iostream>
#include <string.h>
#include <queue>
using namespace std;

struct node{
    int x,y,d;
    node(int _x = 0,int _y = 0,int _d = 0):x(_x),y(_y),d(_d){}
};

const int N(6);
int maze[N][N];
queue<node> Q;
const int dir[4][2] = { {0,1},{1,0},{-1,0},{0,-1} };

bool judge(const int x,const int y){
    return (x>-1&&x<N&&y>-1&&y<N);
}

void output(){
    for(int i=0;i<N;i++){
        for(int j=0;j<N;j++){
            if(j == 0) printf("%d",maze[i][j]);
            else printf(" %d",maze[i][j]);
        }
        printf("\n");
    }
    printf("\n");
}

void deal(const int x,const int y){
    maze[x][y] = 0;
    for(int i=0;i<4;i++){
        int xx = x+dir[i][0];
        int yy = y+dir[i][1];
        if(judge(xx, yy)){
            Q.push(node(xx,yy,i));
        }
    }
}

void found(){
    for(int i=0;i<N;i++){
        for(int j=0;j<N;j++){
            if(maze[i][j] > 4) deal(i, j);
        }
    }
}

void bfs(){
    while (!Q.empty()) {
        size_t size = Q.size();
        while(size--){
            node now = Q.front();
            Q.pop();

            if(maze[now.x][now.y] > 0) maze[now.x][now.y]+=1;
            else{
                int afterx = now.x+dir[now.d][0];
                int aftery = now.y+dir[now.d][1];
                if(judge(afterx, aftery)){
                    Q.push(node(afterx,aftery,now.d));
                }
            }
        }
        found();
    }
}


int main(int argc, const char * argv[]) {
//    freopen("in.txt", "r", stdin);
    int a,m,x,y;
    while(~scanf("%d",&a)){
        maze[0][0] = a;
        for(int i=1;i<N;i++){
            scanf("%d",&maze[0][i]);
        }

        for(int i=1;i<N;i++){
            for(int j=0;j<N;j++){
                scanf("%d",&maze[i][j]);
            }
        }

        scanf("%d",&m);
        while(m--){
            scanf("%d%d",&x,&y);
            --x,--y;
            if(maze[x][y] < 4) maze[x][y]+=1;
            else{
                deal(x,y);
                bfs();
            }
        }
        output();
    }
    return 0;
}

